class MarvelService {
    getResource = async (url) => {
        let res = await fetch(url);
        if(!res.ok) {
            throw new Error(`Couln't fetch ${url}, status: ${res.status}`);
        }
        return await res.json();
    }

    getAllCharacters = () => {
        return this.getResource('https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=49c1311a2f3a774cfb9d22ac5af429bf');
    }

    getCharacter = (id) => {
        return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=49c1311a2f3a774cfb9d22ac5af429bf`);
    }
}

export default MarvelService;
